import React from 'react';
import {
    Table,
    Panel,
    FormGroup,
    FormControl,
    Form,
    Col,
    Checkbox
} from 'react-bootstrap';
import {
    get
} from '../../api/PresenceApi';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            presences: [],
        }
    }

    async componentDidMount() {
        let result = [];
        try {
            result = await get();
        } catch (e) {
            console.log(e);
        }

        this.setState({
            presences: result,
        });
    }

    render() {
        const presences = this.state.presences;
        return ( <
            div >
            <
            Panel bsStyle = "default" >
            <
            Panel.Heading >
            <
            Panel.Title componentClass = "h2" > Controle de acesso a Sala de Reuniões < /Panel.Title> <
            /Panel.Heading> <
            Panel.Body >
            <
            Form horizontal >
            <
            FormGroup controlId = "formControlFilter" >
            <
            Col sm = {
                4
            }
            md = {
                3
            } >
            <
            FormControl componentClass = "select"
            placeholder = "select" >
            <
            option value = "all" > Exibir tudo < /option> <
            option value = "busy" > Somente ocupados < /option> <
            option value = "free" > Somente disponíveis < /option> <
            /FormControl> <
            /Col> <
            Col sm = {
                3
            } >
            <
            Checkbox > Agrupar por Situação < /Checkbox> <
            /Col> <
            /FormGroup> <
            /Form>                         <
            Table striped bordered hover responsive >
            <
            thead >
            <
            tr >
            <
            th > Inicio da análise < /th> <
            th > Fim da análise < /th> <
            th > Situação < /th> <
            /tr> <
            /thead> <
            tbody > {
                this.renderPresences(presences)
            } <
            /tbody> <
            /Table> <
            /Panel.Body> <
            /Panel> <
            /div>
        )
    }

    renderPresences(presences) {
        return presences.map(presence => {
            return ( <
                tr key = {
                    presence.entryTime.toString()
                } >
                <
                td > {
                    presence.entryTime
                } < /td> <
                td > {
                    presence.exitTime
                } < /td> <
                td > {
                    presence.presence
                } < /td> <
                /tr>
            );
        });
    }
}

export default App;
