import axios from 'axios';
import Presence from '../model/Presence';

const path = 'http://localhost:3001/presences';

export default {
    async get() {
        try {
            const {
                data
            } = await axios.get(this.path);
            return data.map(item => new Presence(item));
        } catch (e) {
            console.log('Erro ao buscar presenças: ', e);
        }
    },

    async post(presence) {
        try {
            await axios.post(this.path, JSON.stringify(presence));
        } catch (e) {
            console.log('Erro ao postar presença: ', e);
        }
    }
}
